from schedule


    and teacher_name like #{queryDTO.teacherName}


    and course_name like #{queryDTO.courseName}


  and date(created_at)=date(#{queryDTO.createdAt})



  order by ${queryDTO.orderBy}


  order by id desc

limit #{offset}, #{limit}

select count(*) from schedule


    and teacher_name like #{queryDTO.teacherName}


    and course_name like #{queryDTO.courseName}


